mapper.select:
SELECT 

FROM pay_company a

WHERE a.id = #{id}

SELECT
FROM pay_company a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.reg_name LIKE '%'||#{regName}||'%' '%'+#{regName}+'%' concat('%',#{regName},'%')
AND a.tel = #{tel}
AND a.addr = #{addr}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM pay_company a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pay_company SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
