mapper.select:
SELECT 

FROM pay_store a

WHERE a.id = #{id}

SELECT
FROM pay_store a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.del_flag = #{delFlag}
AND a.status LIKE '%'||#{status}||'%' '%'+#{status}+'%' concat('%',#{status},'%')
AND a.tel LIKE '%'||#{tel}||'%' '%'+#{tel}+'%' concat('%',#{tel},'%')
AND a.addr LIKE '%'||#{addr}||'%' '%'+#{addr}+'%' concat('%',#{addr},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM pay_store a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pay_store SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
